import React from 'react';

// Define types for our data
type PetGroup = {
  name: string;
  adopted: number;
  remaining: number;
  image: string;
};

type RevenueData = {
  month: string;
  revenue: number;
};

// Hardcoded data
const petGroups: PetGroup[] = [
  { name: 'Dogs', adopted: 45, remaining: 15, image: '/api/placeholder/100/100' },
  { name: 'Cats', adopted: 30, remaining: 20, image: '/api/placeholder/100/100' },
  { name: 'Birds', adopted: 15, remaining: 25, image: '/api/placeholder/100/100' },
  { name: 'Rabbits', adopted: 10, remaining: 10, image: '/api/placeholder/100/100' },
];

const revenueData: RevenueData[] = [
  { month: 'Jan', revenue: 5000 },
  { month: 'Feb', revenue: 7000 },
  { month: 'Mar', revenue: 6000 },
  { month: 'Apr', revenue: 8000 },
  { month: 'May', revenue: 9000 },
  { month: 'Jun', revenue: 10000 },
];

const totalRevenue = revenueData.reduce((sum, month) => sum + month.revenue, 0);
const totalAdopted = petGroups.reduce((sum, group) => sum + group.adopted, 0);

export const AdminDashboard: React.FC = () => {
  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ fontSize: '24px', marginBottom: '20px' }}>Admin Dashboard</h1>
      
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <div style={{ border: '1px solid #ccc', padding: '10px', width: '45%' }}>
          <h2>Total Adoptions</h2>
          <p style={{ fontSize: '24px', fontWeight: 'bold' }}>{totalAdopted}</p>
        </div>
        <div style={{ border: '1px solid #ccc', padding: '10px', width: '45%' }}>
          <h2>Total Revenue</h2>
          <p style={{ fontSize: '24px', fontWeight: 'bold' }}>${totalRevenue.toLocaleString()}</p>
        </div>
      </div>

      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', marginBottom: '20px' }}>
        {petGroups.map((group) => (
          <div key={group.name} style={{ border: '1px solid #ccc', padding: '10px', width: '45%', marginBottom: '20px' }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <img src={group.image} alt={group.name} style={{ width: '50px', height: '50px', marginRight: '10px' }} />
              <div>
                <h3>{group.name}</h3>
                <p>Adopted: {group.adopted}</p>
                <p>Remaining: {group.remaining}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div style={{ border: '1px solid #ccc', padding: '10px' }}>
        <h2>Monthly Revenue</h2>
        <ul>
          {revenueData.map((data) => (
            <li key={data.month}>
              {data.month}: ${data.revenue.toLocaleString()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};
